mapper.select:
SELECT registrant_id, product_no, product_name, product_detail, start_bid_price, hopeful_bid_price, bid_unit, auction_start_time, auction_end_time, bidable_grade, 
 hashtag1, hashtag2, hashtag3, product_img1, product_img2, product_img3, product_img4, product_img5, productViewCount
WHERE product_no = #{value};

SELECT user_id, nick_name, auction_grade
FROM USERS
WHERE user_id = #{value};

mapper.update:
UPDATE AUCTION_PRODUCT
SET auction_end_time = DATE_ADD(auction_end_time, INTERVAL 10 SECOND)
WHERE product_no = #{value};
