mapper.select:
SELECT rating_review_no, ap.product_no AS product_no, registrant_id, product_name, review_reg_date, ROUND((status_rating+price_rating+kindness_rating)/3,2) AS avg_rating
FROM AUCTION_PRODUCT ap, RATING_REVIEW rr
WHERE ap.product_no = rr.product_no AND user_id = #{user.id}
LIMIT #{search.pageSize} OFFSET #{search.offset}

SELECT rc.review_count AS review_count, rating_review_no, ap.registrant_id AS registrant_id, user_id, rr.product_no AS product_no, ap.product_name AS product_name, rating_review_content, review_reg_date, comment, comment_reg_date, ROUND((status_rating+price_rating+kindness_rating)/3,2) AS avg_rating, review_img1, review_delete_flag, comment_delete_flag
FROM RATING_REVIEW AS rr, AUCTION_PRODUCT AS ap, ( SELECT COUNT(rating_review_no) review_count FROM RATING_REVIEW AS rrw, AUCTION_PRODUCT AS apt WHERE registrant_id = #{auctionProduct.registrantId} AND rrw.product_no = apt.product_no AND (review_delete_flag = 'N' OR comment_delete_flag = 'N') ) AS rc
WHERE ap.registrant_id = #{auctionProduct.registrantId} AND rr.product_no = ap.product_no AND (review_delete_flag = 'N' OR comment_delete_flag = 'N') LIMIT #{search.pageSize} OFFSET #{search.offset}

SELECT ROUND( (AVG(status_rating) + AVG(price_rating) + AVG(kindness_rating) )/3,2) AS avg_rating, registrant_id AS user_id
FROM RATING_REVIEW AS rr, AUCTION_PRODUCT AS ap
WHERE rr.product_no = ap.product_no AND registrant_id = #{value}
